// eslint-disable-next-line @typescript-eslint/no-unused-vars
export let logger = (..._args) => { };
export const createLogger = (options) => {
    if (options.logger) {
        logger = (...args) => console.log(`\x1b[34m[iracing-api]\x1b[0m`, ...args);
    }
    else {
        logger = () => { };
    }
};
